import React, { Profiler } from 'react';
// import ReactTable from "react-table";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";  
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";  
import Home from "./Home"
import About from "./About?t=355"
import Service from "./Service"
import Users from "./Contact"
import Navbar from "./Navbar"
import Categories from "./Categories.jsx"
import { Switch, Route, Redirect } from 'react-router-dom';
import { GoogleLogin, GoogleLogout } from 'react-google-login';

class App extends React.Component{
  onSignIn(googleUser) {

    let profile = googleUser.getBasicProfile();

    console.log('ID: ' + profile.getId()); // Do not send to your backend! Use an ID token instead.
    console.log('Name: ' + profile.getName());
    console.log('Image URL: ' + profile.getImageUrl());
    console.log('Email: ' + profile.getEmail()); // This is null if the 'email' scope is not present.
  }


  signOut() {  
    var auth2 = gapi.auth2.getAuthInstance();
    auth2.signOut().then(function () {
      console.log('User signed out.');
    });
  }


  render(){

    return(<>
      <Navbar />
      <div className="container-fluid">
          <div className="raw">
              <div className="col-10 mx-auto abcd">
                <span class="g-signin2" data-onsuccess="onSignIn"></span>
                <span style={{
                  cursor:"pointer",
                  color: "blue"
                }} onclick="signOut();">Sign out</span>
              </div>
          </div>
      </div>
      <div className="container-fluid">
          <div className="raw">
              <div className="col-10 mx-auto">

                <Switch>
                  <Route exact path="/" component={Home} />
                  <Route exact path="/about" component={About} />
                  <Route exact path="/contact" component={Users} />
                  <Route exact path="/service" component={Service} />
                  <Route exact path="/react_website" component={Categories} />
                  <Redirect to="/" />
                </Switch>

              </div>
          </div>
      </div>
      </>);
  };
}

