import React, { Component } from 'react';
import {ReactTable} from "react-table";

import Axios from 'axios';
const api_url = "https://gorest.co.in/public-api/";
const authBearer = "Bearer 133acc5970437f9b6f89cd03b05cddfec5c719201c3614d682b24b7dcb6c9a19";

export default class Categories extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            data : [],
            columns : [],
            message : 'Loading...'
        };

        this.fillDataFromAPI = this.fillDataFromAPI.bind(this);
        this.renderTableData = this.renderTableData.bind(this);

        this.fillDataFromAPI();
    };

    fillDataFromAPI(){
        var classState = this;
        Axios.get(api_url+"categories")
        .then(function(res){
            debugger;
            if(res.status==200){
                classState.setState({data : res.data.data});
            }
        })
        .catch(function(res){
            console.log(res);
        });
    }

    // function test(){
    //     console.log("TEST");
    // }

    deleteCategory(e){
        const delteId = e.target.getAttribute("deleteid");
        Axios.delete(api_url+"categories/"+delteId,{
            headers: {
                'Authorization': authBearer
            }
        })
        .then(function(res){
            alert(res.data.data.message);
        })
        .catch(function(res){
            console.log(res);
        });
    };



    renderTableData(){
        const data = this.state.data.map((cat, index) => {
            const { id, name, description, status} = cat //destructuring
            return (
               <tr key={id}>
                  <td>{id}</td>
                  <td>{name}</td>
                  <td>{description}</td>
                  <td>{status ? "Active" : "Deactive"}</td>
                  <td className="categoty-action-link" deleteid={id} onClick={this.deleteCategory}>DELETE</td>
               </tr>
            )
         });

        return data;
    }


    render(){
        const data = [{
            age: 26,
            visits: 100
          },{
            age: 44,
            visits: 200
          }]

        const tableHeader = '';
        if(this.state.message){

        }else{
            tableHeader = (<tr>
                <th>ID</th>
                <th>NAME</th>
                <th>DESCRIPTION</th>
                <th>STATUS</th>
                <th>ACTION</th>
            </tr>);
        }


        return(
            <div>

                <h1 onClick={this.test}>Categories</h1>

                <table border="1" id='cat'>
                    <tbody>
                        {tableHeader}
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        );
    };

}

