    //         }
    //     })
    //     .then(function(res){
    //         console.log(res);
    //     })
    //     .catch(function(res){
    //         console.log(res);
    //     });
    // };


    renderTableData(){
        return this.state.data.map((cat, index) => {
            const { id, name } = cat //destructuring
            return (
               <tr key={id}>
                  <td>{id}</td>
                  <td>{name}</td>
               </tr>
            )
         })
    }


    render(){
        const data = [{  
            name: 'Ayaan',  
            age: 26  
            },{  
             name: 'Ahana',  
             age: 22  
             },{  
             name: 'Peter',  
             age: 40      
             },{  
             name: 'Virat',  
             age: 30  
             },{  
             name: 'Rohit',  
             age: 32  
             },{  
             name: 'Dhoni',  
             age: 37  
             }]  
         const columns = [{  
           Header: 'Name',  
           accessor: 'name'  
           },{  
           Header: 'Age',  
           accessor: 'age'  
           }]


        return(
            <div>

                <table id='students'>
                    <tbody>
                        {this.renderTableData()}
                    </tbody>
                </table>

                {/* <ReactTable  
                  data={data}  
                  columns={columns}  
                  defaultPageSize = {2}  
                  pageSizeOptions = {[2,4, 6]}  
                /> */}
            </div>
        );
    };

}

export default Categories;
