            visits: 200
          }]
        return(
            <div>
                <h1 onClick={this.test}>Categories</h1>

                <table border="1" id='cat'>
                    <tbody>
                        <tr>
                            <th>ID</th>
                            <th>NAME</th>
                            <th>DESCRIPTION</th>
                            <th>STATUS</th>
                            <th>ACTION</th>
                        </tr>
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        );
    };

}

